``` c
#include <stdio.h>

typedef unsigned char *byte_pointer;

int is_little_endian()
{
    int val = 0x00000001;
    byte_pointer valp = (byte_pointer)&val;
    int temp;
    temp = valp[0];
    if (temp == 1)
        return 1;
    else
        return 0;
}

int main()
{
    int result = is_little_endian();
    printf("is_little_endian: %d", result);
    return 1;
}
